import React, { useState } from 'react';
import './App.css'; // Assurez-vous d'avoir ce fichier pour les styles

function App() {
  const [theme, setTheme] = useState('light'); // 'light' ou 'dark'
  const [selectedLink, setSelectedLink] = useState(null); // √âtat pour g√©rer le lien s√©lectionn√©

  const changeTheme = (newTheme) => {
    setTheme(newTheme);
  };

  const backgroundColor = theme === 'light' ? '#f5f5dc' : '#333'; // Blanc cass√© pour clair, sombre pour sombre
  const textColor = theme === 'light' ? '#000' : '#fff'; // Texte noir pour clair, blanc pour sombre

  // D√©finir les liens et descriptions pour chaque bouton
  const links = [
    { url: 'https://top-stream.tv/', description: 'Lien vers le site Top Stream', title: 'Top Stream', logo: 'logo-url-here' },
    { url: 'https://example.com/2', description: 'Description pour le lien 2', title: 'Lien 2', logo: 'logo-url-here' },
    { url: 'https://example.com/3', description: 'Description pour le lien 3', title: 'Lien 3', logo: 'logo-url-here' },
    { url: 'https://example.com/4', description: 'Description pour le lien 4', title: 'Lien 4', logo: 'logo-url-here' },
    { url: 'https://example.com/5', description: 'Description pour le lien 5', title: 'Lien 5', logo: 'logo-url-here' },
    // Ajoutez d'autres liens et descriptions ici
  ];

  const handleButtonClick = (link) => {
    setSelectedLink(link); // Met √† jour le lien s√©lectionn√©
  };

  const closeModal = () => {
    setSelectedLink(null); // Ferme le modal
  };

  return (
    <div style={{ backgroundColor, color: textColor, minHeight: '100vh', padding: '20px' }}>
      <h1 className="text-3xl font-bold mb-4">Mon Site React</h1>
      <div className="flex flex-wrap justify-around">
        {links.map((link, index) => (
          <button 
            key={index} 
            className="m-2 p-3 bg-blue-600 text-white rounded-lg shadow hover:bg-blue-700 transition duration-300"
            onClick={() => handleButtonClick(link)} // Affiche le modal avec les informations
          >
            Bouton {index + 1}
          </button>
        ))}
      </div>

      {/* Affichage du modal avec les informations du lien s√©lectionn√© */}
      {selectedLink && (
        <div className={`fixed inset-0 flex items-center justify-center z-50 ${theme === 'light' ? 'bg-white' : 'bg-gray-800'} bg-opacity-90`}>
          <div className={`p-6 rounded-lg shadow-lg ${theme === 'light' ? 'bg-white' : 'bg-gray-700'}`}>
            <button onClick={closeModal} className="absolute top-2 right-2 text-gray-500 hover:text-gray-700">‚úñÔ∏è</button>
            <img src={selectedLink.logo} alt="Logo" className="w-16 h-16 rounded-full" />
            <h2 className="text-xl font-bold">{selectedLink.title}</h2>
            <p className="text-sm">{selectedLink.description}</p>
            <button 
              onClick={() => window.location.href = selectedLink.url} 
              className="mt-4 w-full p-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-300"
            >
              Visiter le site
            </button>
          </div>
        </div>
      )}

      <div className="mt-4">
        <button onClick={() => changeTheme('light')} className="p-2 bg-yellow-300 rounded-full shadow hover:bg-yellow-400">
          ‚òÄÔ∏è
        </button>
        <button onClick={() => changeTheme('dark')} className="p-2 bg-gray-800 text-white rounded-full shadow hover:bg-gray-700">
          üåô
        </button>
      </div>
    </div>
  );
}

export default App;
