import React, { useState, useEffect } from "react";
import "./App.css";

const getScreenshotUrl = (url) => {
  return `https://api.urlbox.io/v1/RPlxApG0kQQ6UiAm/png?url=${encodeURIComponent(
    url
  )}&width=800&block_ads=true`;
};

const sites = [
  {
    name: "Mavanimes",
    url: "https://www.mavanimes.co/",
    image: getScreenshotUrl("https://www.mavanimes.co/"),
    description: "Anime - Adblock/uBlock fonctionne, VOSTFR uniquement.",
  },
  {
    name: "Top Stream",
    url: "https://top-stream.tv/",
    image: getScreenshotUrl("https://top-stream.tv/"),
    description: "Films Séries Animes - ",
  },
  {
    name: "Titrov",
    url: "https://titrov.com/13tt4k6cj/home/titrov",
    image: getScreenshotUrl("https://titrov.com/13tt4k6cj/home/titrov"),
    description: "Description du site 3.",
  },
  {
    name: "Soap2Day",
    url: "https://ww25.soap2day.day/",
    image: getScreenshotUrl("https://ww25.soap2day.day/"),
    description: "Description du site 4.",
  },
  {
    name: "Wiflix HUB",
    url: "https://wiflix.name/",
    image: getScreenshotUrl("https://wiflix.name/"),
    description: "Description du site 5.",
  },
  {
    name: "Vredap",
    url: "https://www.vredap.com/51bpuyl61bv0up8/home/vredap",
    image: getScreenshotUrl(
      "https://www.vredap.com/51bpuyl61bv0up8/home/vredap"
    ),
    description: "Description du site 6.",
  },
  {
    name: "Lokarn",
    url: "https://www.lokarn.net/films",
    image: getScreenshotUrl("https://www.lokarn.net/films"),
    description: "Description du site 7.",
  },
  {
    name: "Gogoflix",
    url: "https://gogoflix.space/",
    image: getScreenshotUrl("https://gogoflix.space/"),
    description: "Description du site 8.",
  },
  {
    name: "Pigraz",
    url: "https://www.pigraz.com/8ajlyll5ccv9o/home/pigraz",
    image: getScreenshotUrl("https://www.pigraz.com/8ajlyll5ccv9o/home/pigraz"),
    description: "Description du site 9.",
  },
  {
    name: "Vogfo",
    url: "https://vogfo.com/kp6vx7yqukgh7/home/vogfo/",
    image: getScreenshotUrl("https://vogfo.com/kp6vx7yqukgh7/home/vogfo/"),
    description: "Description du site 10.",
  },
  {
    name: "PapaDuStream v2",
    url: "https://papadustream.vc/",
    image: getScreenshotUrl("https://papadustream.vc/"),
    description: "Description du site 11.",
  },
  {
    name: "Senpai Stream",
    url: "https://senpai-stream.net/",
    image: getScreenshotUrl("https://senpai-stream.net/"),
    description: "Description du site 12.",
  },
  {
    name: "FilmoFlix",
    url: "https://filmoflix.ad/",
    image: getScreenshotUrl("https://filmoflix.ad/"),
    description: "Description du site 13.",
  },
  // {
  //   name: "",
  //   url: "",
  //   image: getScreenshotUrl(""),
  //   description: "Description du site 14.",
  // },
  // {
  //   name: "",
  //   url: "",
  //   image: getScreenshotUrl(""),
  //   description: "Description du site 15.",
  // },
];

const App = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [showLinksPopup, setShowLinksPopup] = useState(false);

  const handleOpenLinksPopup = () => {
    setShowLinksPopup(true);
  };

  useEffect(() => {
    const savedTheme = localStorage.getItem("theme");
    if (savedTheme === "dark") {
      setDarkMode(true);
    }
  }, []);

  const toggleTheme = () => {
    const newTheme = !darkMode;
    setDarkMode(newTheme);
    localStorage.setItem("theme", newTheme ? "dark" : "light");
  };

  return (
    <div
      className={`min-h-screen ${
        darkMode ? "bg-gray-900 text-white" : "bg-[#FAF9F6] text-black"
      }`}
    >
      <div className="App p-4">
        {/* Bouton pour changer de thème */}
        <div className="flex justify-end mb-4">
          <button
            onClick={toggleTheme}
            className="px-4 py-2 rounded bg-blue-500 text-white hover:bg-blue-700"
          >
            {darkMode ? "Mode Clair" : "Mode Sombre"}
          </button>
        </div>

        {/* Bouton pour ouvrir la pop-up avec 3 liens */}
        <button
          onClick={handleOpenLinksPopup}
          className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-700"
        >
          Ouvrir Pop-up avec liens
        </button>

        {/* Pop-up avec 3 liens */}
        {showLinksPopup && (
          <div className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-50 flex justify-center items-center">
            <div
              className={`p-6 rounded shadow-lg w-96 ${
                darkMode ? "bg-gray-800 text-white" : "bg-[#FAF9F6] text-black"
              }`}
            >
              <h2 className="text-xl font-bold mb-4">
                Redirection vers des sites
              </h2>
              <div className="mt-4 space-y-2">
                <button
                  onClick={() =>
                    window.open("https://www.mavanimes.co/", "_blank")
                  }
                  className="w-full py-2 bg-blue-500 text-white rounded hover:bg-blue-700"
                >
                  Mavanimes
                </button>
                <button
                  onClick={() =>
                    window.open("https://top-stream.tv/", "_blank")
                  }
                  className="w-full py-2 bg-green-500 text-white rounded hover:bg-green-700"
                >
                  Top Stream
                </button>
                <button
                  onClick={() =>
                    window.open("https://www.titrov.com/", "_blank")
                  }
                  className="w-full py-2 bg-purple-500 text-white rounded hover:bg-purple-700"
                >
                  Titrov
                </button>
              </div>
              <button
                onClick={() => setShowLinksPopup(false)}
                className="mt-4 bg-red-500 text-white px-4 py-2 rounded"
              >
                Fermer
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
