import React, { useState } from 'react';
import './App.css'; // Assurez-vous d'avoir ce fichier pour les styles

function App() {
  const [theme, setTheme] = useState('light'); // 'light' ou 'dark'

  const changeTheme = (newTheme) => {
    setTheme(newTheme);
  };

  const backgroundColor = theme === 'light' ? '#f5f5dc' : '#333'; // Blanc cass√© pour clair, sombre pour sombre
  const textColor = theme === 'light' ? '#000' : '#fff'; // Texte noir pour clair, blanc pour sombre

  // D√©finir les liens et descriptions pour chaque bouton
  const links = [
    { url: 'https://top-stream.tv/', description: 'Lien vers le site Top Stream' },
    { url: 'https://example.com/2', description: 'Description pour le lien 2' },
    { url: 'https://example.com/3', description: 'Description pour le lien 3' },
    { url: 'https://example.com/4', description: 'Description pour le lien 4' },
    { url: 'https://example.com/5', description: 'Description pour le lien 5' },
    // Ajoutez d'autres liens et descriptions ici
  ];

  const handleButtonClick = (link) => {
    alert(link.description); // Affiche une pop-up avec la description
    // Pour rediriger, d√©commentez la ligne suivante
    // window.location.href = link.url;
  };

  return (
    <div style={{ backgroundColor, color: textColor, minHeight: '100vh', padding: '20px' }}>
      <h1 className="text-3xl font-bold mb-4">Mon Site React</h1>
      <div className="flex flex-wrap justify-around">
        {links.map((link, index) => (
          <button 
            key={index} 
            className="m-2 p-2 bg-blue-500 text-white rounded hover:bg-blue-600"
            onClick={() => handleButtonClick(link)} // Affiche la pop-up avec la description
          >
            Bouton {index + 1}
          </button>
        ))}
      </div>
      <div className="mt-4">
        <button onClick={() => changeTheme('light')} className="p-2 bg-yellow-300 rounded-full shadow hover:bg-yellow-400">
          ‚òÄÔ∏è
        </button>
        <button onClick={() => changeTheme('dark')} className="p-2 bg-gray-800 text-white rounded-full shadow hover:bg-gray-700">
          üåô
        </button>
      </div>
    </div>
  );
}

export default App;
