import React, { useState } from "react";
import "./App.css";

const sites = [
  {
    name: "Site 1",
    url: "https://www.site1.com",
    image: "/images/image1.jpg",
    description: "Description du site 1.",
  },
  {
    name: "Site 2",
    url: "https://www.site2.com",
    image: "/images/image2.jpg",
    description: "Description du site 2.",
  },
  // Ajoutez plus de sites ici
];

function App() {
  const [hoveredSite, setHoveredSite] = useState(null);
  const [modalSite, setModalSite] = useState(null);

  return (
    <div className="App">
      <h1 className="text-center text-3xl font-bold mb-6">Liste de Sites</h1>
      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        {sites.map((site, index) => (
          <button
            key={index}
            className="relative bg-blue-500 text-white p-4 rounded hover:bg-blue-700"
            onMouseEnter={() => setHoveredSite(site)}
            onMouseLeave={() => setHoveredSite(null)}
            onClick={() => setModalSite(site)}
          >
            {site.name}
          </button>
        ))}
      </div>

      {/* Pr√©visualisation */}
      {hoveredSite && (
        <div className="fixed bottom-10 right-10 bg-white p-4 shadow-lg rounded">
          <img
            src={hoveredSite.image}
            alt={hoveredSite.name}
            className="w-48 h-32 object-cover mb-2"
          />
          <p className="text-sm">{hoveredSite.description}</p>
        </div>
      )}

      {/* Pop-up */}
      {modalSite && (
        <div className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4">{modalSite.name}</h2>
            <p>{modalSite.description}</p>
            <a
              href={modalSite.url}
              target="_blank"
              rel="noopener noreferrer"
              className="block mt-4 text-blue-500 underline"
            >
              Visitez le site
            </a>
            <button
              onClick={() => setModalSite(null)}
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded"
            >
              Fermer
            </button>
          </div>
        </div>
      )}
      <Footer />
    </div>
  );
}

export default App;
