function App() {
  const [hoveredLink, setHoveredLink] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  const sites = [
    {
      name: "Site 1",
      links: [
        { label: "Lien 1", url: "https://example.com/1" },
        { label: "Lien 2", url: "https://example.com/2" },
      ],
    },
    {
      name: "Site 2",
      links: [
        { label: "Lien A", url: "https://example.com/a" },
        { label: "Lien B", url: "https://example.com/b" },
      ],
    },
  ];

  // Pour la prévisualisation (ici je mets un placeholder d'image)
  const getScreenshotUrl = (url) =>
    `https://via.placeholder.com/300?text=Preview+of+${url}`;

  return (
    <div
      className={`min-h-screen ${
        darkMode ? "bg-gray-900 text-white" : "bg-[#FAF9F6] text-black"
      }`}
    >
      <div className="grid grid-cols-2 gap-4">
        {sites.map((site, index) => (
          <div key={index} className="bg-blue-500 text-white p-4 rounded">
            <h2>{site.name}</h2>
            {site.links.map((link, idx) => (
              <div key={idx} className="relative">
                <button
                  onMouseEnter={() => setHoveredLink(link)} // Lors du survol, on définit l'URL
                  onMouseLeave={() => setHoveredLink(null)} // Lorsqu'on quitte le survol, on réinitialise
                  className="w-full py-2 bg-blue-700 text-white rounded hover:bg-blue-900"
                >
                  {link.label}
                </button>
              </div>
            ))}
          </div>
        ))}
      </div>

      {/* Affichage de la prévisualisation de l'URL sur le survol */}
      {hoveredLink && (
        <div className="absolute top-0 left-full ml-4 bg-white text-black p-4 shadow-lg rounded w-60">
          <img
            src={getScreenshotUrl(hoveredLink.url)} // Génère l'image de prévisualisation
            alt={hoveredLink.label}
            className="w-full h-auto object-cover mb-2 rounded"
          />
          <p>{hoveredLink.label}</p>
        </div>
      )}
    </div>
  );
}

export default App;
