import React, { useState } from 'react';
import './App.css'; // Assurez-vous d'avoir ce fichier pour les styles

function App() {
  const [theme, setTheme] = useState('light'); // 'light' ou 'dark'

  const changeTheme = (newTheme) => {
    setTheme(newTheme);
  };

  const backgroundColor = theme === 'light' ? '#f5f5dc' : '#333'; // Blanc cassé pour clair, sombre pour sombre
  const textColor = theme === 'light' ? '#000' : '#fff'; // Texte noir pour clair, blanc pour sombre

  return (
    <div style={{ backgroundColor, color: textColor, minHeight: '100vh', padding: '20px' }}>
      <h1>Mon Site React</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around' }}>
        {[...Array(20)].map((_, index) => (
          <button key={index} style={{ margin: '10px' }} onClick={() => alert('Lien à ajouter')}>
            Bouton {index + 1}
          </button>
        ))}
      </div>
      <div>
        <button onClick={() => changeTheme('light')}>Thème Clair</button>
        <button onClick={() => changeTheme('dark')}>Thème Sombre</button>
      </div>
    </div>
  );
}

export default App;
